import React, { useState } from "react";
import MealList from "./MealList";

function App() {
  const [mealData, setMealData] = useState(null);
  const [calories, setCalories] = useState(2000);

  function getMealData() {
    fetch(
      `https://api.spoonacular.com/mealplanner/generate?apiKey=a30b71fdfbeb4d85b8708e9064927887&timeFrame=day&targetCalories=${calories}`
    )
      .then((response) => response.json())
      .then((data) => {
        setMealData(data);
        console.log(data);
      })
      .catch(() => {});
  }

  function handleChange(e) {
    setCalories(e.target.value);
  }

  return (
    <div className="App">
      <section className="controls">
        <input
          type="text"
          style={{ width: 500 }}
          placeholder="What do you want your meals to include? (e.g. 'Chicken')"
          onChange={handleChange}
        ></input>
      </section>
      <button onClick={getMealData}>Get Meals for the week!</button>
      {mealData && <MealList mealData={mealData} />}
    </div>
  );
}

export default App;
